INSERT INTO `wp_options` VALUES (160,'theme_mods_foce','a:6:{i:0;b:0;s:18:\"nav_menu_locations\";a:0:{}s:18:\"custom_css_post_id\";i:-1;s:5:\"story\";s:733:\"Loin, très loin, au delà des monts Karma, à mille lieues des pays Mizukage et Kagura, demeurent les Nekos Nekos. Ils vivent en retrait, sur les côtes du Funato, un vaste océan d’arbres fruitier. Un petit ruisseau coule en leur lieu et les approvisionne en règlalades nécessaires en tout genre; un pays paradisiaque... Sauf pour Kawaneko, le premier chat à avoir développé une intolérance à la fleur d’oranger, qui malheureusement pour lui est la base de toute productions du pays. Serait-ce une maladie, un virus, le début d’une pandémie ou un sort jeté par le sorcier Jaakuna ? Kawaneko et sa bande partent en quête de réponses pour arrêter la malveillant Jaakuna et retrouver l’ordre chez les Nekos Nekos.\";s:5:\"place\";s:352:\"Le pays fantastique des Nekos Nekos : Nekosnotochi. Loin, très loin, au delà des monts Karma, à mille lieues des pays Mizukage et Kagura. Ils vivent en retrait, sur les côtes du Funato, un vaste océan d’arbres fruitier. Un petit ruisseau coule en leur lieu et les approvisionne en règlalades nécessaires en tout genre; un pays paradisiaque... \";s:16:\"sidebars_widgets\";a:2:{s:4:\"time\";i:1721920856;s:4:\"data\";a:2:{s:19:\"wp_inactive_widgets\";a:0:{}s:9:\"sidebar-1\";a:5:{i:0;s:7:\"block-2\";i:1;s:7:\"block-3\";i:2;s:7:\"block-4\";i:3;s:7:\"block-5\";i:4;s:7:\"block-6\";}}}}','off');
